<?php


namespace Gone\Subligraphy\Helper;

use Magento\Eav\Model\Config;
use Magento\Framework\App\Filesystem\DirectoryList;
use Magento\Framework\Exception\NoSuchEntityException;
use Magento\Framework\Filesystem;
use Magento\Framework\Math\Random;
use Magento\Framework\App\Helper\Context;
use Gone\Base\Helper\CoreConfigData;
use Magento\Customer\Model\Session;
use Magento\Store\Model\StoreManagerInterface;

class SubligraphyConfig extends \Magento\Framework\App\Helper\AbstractHelper
{
    const CERTIFICATE_MEDIA_BASE_URL = '/subligraphy/certificate/';
    const MAX_IMG_SIZE = 2; //Mo
    const MAX_CERTIFICATE_COPY_COUNT = 'subligraphy/general/max_copy_count';
    const SUBLIGRAPHY_LOGO_B64 ='data:image/png;base64,';
    public const SUBLIGRAPHY_LOGO_CARTOUCHE_B64 = 'data:image/png;base64,';
    public const RANDOM_CHARS = 'ACEFGHJKMNPRTUVWXY';

    /**
     * Types de digits.
     */
    public const STDLIB_RANDOM_CHAR_TYPE = [
        'Alphabetic' => 1,
        'Numbers' => 2,
        'Both' => 3,
        'Hexa' => 4
    ];

    /**
     * Types de casse.
     */
    public const STDLIB_RANDOM_CHAR_CASE = [
        'Lower' => 1,
        'Upper' => 2,
        'Mixed' => 3
    ];

    private Session $_customerSession;
    private CoreConfigData $_coreConfigHelper;
    protected Random $_random;
    protected StoreManagerInterface $_storeManager;
    protected Filesystem $_filesystem;
    protected $_eavConfig;

    public function __construct(
        Context $context,
        Session $session,
        Random $_random,
        Filesystem $filesystem,
        CoreConfigData $coreConfigHelper,
        StoreManagerInterface $storeManager,
        Config $eavConfig
    ) {
        parent::__construct($context);
        $this->_customerSession = $session;
        $this->_random = $_random;
        $this->_coreConfigHelper = $coreConfigHelper;
        $this->_storeManager=$storeManager;
        $this->_filesystem = $filesystem;
        $this->_eavConfig = $eavConfig;
    }

    /**
     * @return bool
     */
    public function isSubligraphAuth():bool
    {
        $isLogged = $this->_customerSession->isLoggedIn();
        $isSubligraph =  $isLogged ? $this->_customerSession->getCustomer()->getIsSubligraph() : false;
        if ($isSubligraph) {
            return true;
        }
        return false;
    }


    /**
     * @param $url
     * @return string
     * @throws NoSuchEntityException
     */
    public function getImage($url):string
    {
        $path = $this->_filesystem->getDirectoryRead(DirectoryList::VAR_DIR);
        $fullPath = $path->getAbsolutePath(self::CERTIFICATE_MEDIA_BASE_URL . $url);
        if (file_exists($fullPath)) {
            $ext = pathinfo($fullPath, PATHINFO_EXTENSION);
            $image = file_get_contents($fullPath);
            return 'data:image/'.$ext.';base64,'.base64_encode($image) ?? '';
        }
        return false;
    }

    /**
     * @return string
     */
    public function getNewCertificateNumber()
    {

        $attribute = $this->_eavConfig->getEntityType('subligraphy_base_number');
        $number = $attribute->fetchNewIncrementId();
        $number = $this->_getRandomString(1) . $number .  self::getRandomString(2, self::STDLIB_RANDOM_CHAR_TYPE['Numbers']);
        $number = self::right(self::getRandomString(15, self::STDLIB_RANDOM_CHAR_TYPE['Numbers']) . $number, 15);
        $number = $this->_getRandomString(1) . $number;


        return  self::mid($number, 0, 4)
            . '-' .    self::mid($number, 4, 4)
            . '-' .    self::mid($number, 8, 4)
            . '-' .    self::mid($number, 12, 4)
            ;
    }

    /**
     * @return []
     */
    public function getCertificateDirUrl()
    {
        $subPath =    self::getRandomString(4, self::STDLIB_RANDOM_CHAR_TYPE['Both'], self::STDLIB_RANDOM_CHAR_CASE['Mixed'])
            . '-'
            . self::getRandomString(4, self::STDLIB_RANDOM_CHAR_TYPE['Both'], self::STDLIB_RANDOM_CHAR_CASE['Mixed'])
            . '-'
            . self::getRandomString(4, self::STDLIB_RANDOM_CHAR_TYPE['Both'], self::STDLIB_RANDOM_CHAR_CASE['Mixed'])
            . '-'
            . str_replace('.', '_', microtime(true));

        $imgPath = $this->_customerSession->getCustomerId() . '/' . $subPath;
        $uploadPath = self::CERTIFICATE_MEDIA_BASE_URL . $imgPath;

        return [
            'uploadTo'=>$uploadPath,
            'displayFrom'=>$imgPath
            ];
    }



    /***************** GET FROM MAGENTO 1 STATIC CLASS *************/
    /**
     * @param $count
     * @return string
     */
    protected function _getRandomString($count)
    {

        $back = '';

        for ($i=0; $i<$count; $i++) {
            $back .= self::mid(self::RANDOM_CHARS, rand(0, strlen(self::RANDOM_CHARS)-1), 1);
        }
        echo 'back:'.$back;
        return $back;
    }

    /**
     * Extrait le nombre de caractres spcifis situs  droite
     * d'une chane.
     *
     * @param string $str : chane de caractres source.
     * @param int $length : nombre de caractres souhaits.
     *
     * @return string : la sous-chane extraite.
     */
    public static function right($str, $length)
    {
        $length = max(0, $length);
        return substr($str, max(0, strlen($str) - $length), min($length, strlen($str)));
    }

    /**
     * Force la valeur passe en argument  une valeur comprise entre
     * une valeur min et une valeur max.
     *
     * @param mixed $value : valeur source.
     * @param $valueMin : valeur min souhaite.
     * @param $valueMax : valeur max souhaite.
     *
     * @return mixed : la valeur obtenue.
     */
    public static function minmax($value, $valueMin, $valueMax)
    {
        if ($valueMin > $valueMax) {
            return max(min($value, $valueMin), $valueMax);
        } else {
            return max(min($value, $valueMax), $valueMin);
        }
    }

    /**
     * Extrait une sous-chane d'une chane de caractres.
     *
     * @param string $str : chane de caractres source.
     * @param int $startIndex : position de la sous-chane (premier caractre : 0).
     * @param int $length : (facultatif, null) longueur souhaite de la sous-chane.
     *
     * @return string : la sous-chane extraite.
     */
    public static function mid($str, $startIndex, $length = null)
    {
        $len = strlen($str);
        if ($length === null) {
            $length = $len;
        }
        if ($startIndex >= $len) {
            return '';
        }
        $startIndex = max(0, $startIndex);
        $length = self::minmax($length, 0, $len - $startIndex);
        return substr($str, $startIndex, $length);
    }

    /**
     * Construit une chane compose de caractres alatoires.
     *
     * @param string $charCount : nombre de caractres souhait.
     * @param STDLIB_RANDOM_CHAR_TYPE $charType : (facultatif, STDLIB_RANDOM_CHAR_TYPE::Both) type des caractres.
     * @param STDLIB_RANDOM_CHAR_CASE $charCase : (facultatif, STDLIB_RANDOM_CHAR_CASE::Lower) casse des caractres.
     *
     * @return string : la chane de caractres obtenue.
     */
    public static function getRandomString($charCount, $charType = self::STDLIB_RANDOM_CHAR_TYPE['Both'], $charCase = self::STDLIB_RANDOM_CHAR_CASE['Lower'])
    {

        $result = '';
        $i = 0;

        switch ($charType) {
            case self::STDLIB_RANDOM_CHAR_TYPE['Alphabetic']:
                for ($i = 1; $i <= $charCount; $i++) {
                    $result .= chr(rand(97, 122) - (rand() & 1) * 32);
                }
                break;
            case self::STDLIB_RANDOM_CHAR_TYPE['Numbers']:
                for ($i = 1; $i <= $charCount; $i++) {
                    $result .= chr(rand(48, 57));
                }
                break;
            default:
                $maxAlphaCode = ($charType == self::STDLIB_RANDOM_CHAR_TYPE['Hexa']) ? 102 : 122;
                for ($i = 1; $i <= $charCount; $i++) {
                    if ((rand(1, 16)) > 8) {
                        $result .= chr(rand(48, 57));
                    } else {
                        $result .= chr(rand(97, $maxAlphaCode) - (rand() & 1) * 32);
                    }
                }
                break;
        }
        switch ($charCase) {
            case self::STDLIB_RANDOM_CHAR_CASE['Upper']:
                $result = strtoupper($result);
                break;
            case self::STDLIB_RANDOM_CHAR_CASE['Lower']:
                $result = strtolower($result);
                break;
        }

        return ($result);
    }
}
